mapper.select:
select A.facility_id, A.facility_name, A.court, A.product, A.facility_status, A.facility_images,
    B.file_name
from facility A left outer join facility_attach B on A.facility_id = B.facility_id;

mapper.update:
update facility set facility_name=#{facility_name}, open_time=#{open_time}, close_time=#{close_time},
default_time=#{default_time}, basic_fee=#{basic_fee}, extra_fee=#{extra_fee}, contact=#{contact},
info=#{info}, caution=#{caution}, court=#{court}, product=#{product}, facility_status=#{facility_status}
where facility_id=#{facility_id}
