mapper.select:
SELECT * FROM user
WHERE user_id = #{user_id}
AND user_name = #{user_name}
AND user_phone = #{user_phone}

mapper.update:
UPDATE user
SET user_status = #{userStatus}
WHERE user_id = #{userId}

UPDATE user
SET address = #{address}, phone = #{phone}
WHERE user_id = #{userId}

UPDATE user
SET level = #{level}
WHERE user_id = #{userId}
