mapper.update:
UPDATE
    product
SET
    stock = stock - #{count}
WHERE
    product_id = #{product_id}

UPDATE
    product
SET
    stock = stock + #{count}
WHERE
    product_id = #{product_id}

UPDATE rental
SET rental_status = #{rental_status}
WHERE rental_id = #{rental_id}

UPDATE rental
SET rental_status = false
WHERE rental_id = #{rental_id}
